  //     const randomCountry = Math.floor(Math.random() * areas.length)

  //     setCountry(areas[randomCountry]);
  //   // }
  // }

  const url2 = `https://themealdb.com/api/json/v1/1/random.php?a=${country}`;
  console.log(country)

  //function to fetch data from MealDB
  const recipePage = () => {
    fetch(url2, { method: 'GET' })
      .then((data) => data.json())
      .then((data) => {
        console.log(data.meals[0].strMeal)
        setMeal(data.meals[0])
      })
      .catch((error) => console.log(error));
  }



  return (
    <BrowserRouter>
    <div className="App">
      <header className="App-header">
      <img src='logo.jpg' width='200px' alt="logo"></img>
      <h1>Food</h1>
      </header>
    </div>
    <p>blah blah blah food dofjakjndfcikjabefkjbawefjh</p>
      <button onClick={recipePage}>Get Random Cultural Dish</button>
      <div>{meal.str}</div>
      {/* <div>Instruction: {meal.strInstructions}</div>
      <img src={meal.strMealThumb} /> */}
      {/* <button onClick={newPage}>Ready to start</button> */}

      <Link to="/recipe">Recipe</Link>
      <Routes>
        <Route path="/recipe" element={<Recipe/>}/>
        {/* <Recipe /> */}
    </Routes>

   </BrowserRouter>
  );

}

export default App;
