import React, { useState } from 'react'
function Recipe({ meal }) {
    //const [meal, setMeal] = useState('')
    const newArr= [];

    for (let i = 1; i <= 20; i++){
        if (meal[`strIngredient${i}`])
            newArr.push(meal[`strIngredient${i}`])
    }
    console.log(newArr)
    // console.log(meal.strIngredient1)

    return (
        //<div>Recipe Page</div>

                <div className="Recipe">
                <header className="Recipe-header">

                <h1>Today's Recipe:</h1>
                </header>


                <div>


                <div>{meal.strMeal}</div>
                <div>Culture: {meal.strArea}</div>
                <div>Ingredients: <li></li>{newArr}</div>
                <div>Instruction: {meal.strInstructions}</div>
                <img src={meal.strMealThumb} />



                </div>

                </div>

            );
    }

export default Recipe;
